int n, nn = 1;

void update(int k, int a, int c[], int f) {
  k += n - 1;
  c[k] = a;

  while(k > 0) {
    k = (k - 1) / 2;
    if(f) c[k] = max(c[k * 2 + 1], c[k * 2 + 2]);
    else c[k] = min(c[k * 2 + 1], c[k * 2 + 2]);
  }
}

int query(int a, int b, int k, int l, int r, int c[], int f) {
  if(r <= a || b <= l) return f ? 0 : INT_MAX;
  if(a <= l && r <= b) return c[k];

  int vl = query(a, b, k * 2 + 1, l, (l + r) / 2, c, f);
  int vr = query(a, b, k * 2 + 1, (l + r) / 2, r, c, f);

  return f ? max(vl, vr) : min(vl, vr);
}

