inline int query(int a, int b, int k, int l, int r, int p1, int p2) {
	if (r <= a || b <= l) { if (p2 == 0)return 1 << 30; return -(1 << 30); }
	if (a <= l && r <= b) return dat[p1][p2][k]; k <<= 1;
	int lc = query(a, b, k, l, (l + r) / 2, p1, p2);
	int rc = query(a, b, k + 1, (l + r) / 2, r, p1, p2);
	if (p2 == 0)return min(lc, rc);
	if (p2 == 1)return max(rc, lc);
}
inline int rmqw(int x, int y, int p) {
	int V1 = query(x, y + 1, 1, 0, size_, p, 1), V2 = query(x, y + 1, 1, 0, size_, p, 0);
	return V1 - V2;
}
