long min(long a, long b)
{
    return a < b ? a : b;
}

long max(long a, long b)
{
    return a > b ? a : b;
}

struct Star
{
    long x, y, b;
public:
    Star(long x, long y, long b): x(x), y(y), b(b) {}
    bool operator<(const Star& s)
    {
        return b < s.b;
    }
};

struct Range
{
    long x1, y1, x2, y2, b;
public:
    Range(const Star& s): x1(s.x), y1(s.y), x2(s.x), y2(s.y), b(s.b) {}
    void add(const Star& s)
    {
        x1 = min(x1, s.x);
        y1 = min(y1, s.y);
        x2 = max(x2, s.x);
        y2 = max(y2, s.y);
    }
    void add(const Range& r)
    {
        x1 = min(x1, r.x1);
        y1 = min(y1, r.y1);
        x2 = max(x2, r.x2);
        y2 = max(y2, r.y2);
    }
    long area()
    {
        return (x2 - x1) * (y2 - y1);
    }
};

