        long j;
        for (j = i; ranges[j].b - ranges[i].b <= d && j < ranges.size(); j++)
        {
            range.add(ranges[j]);
        }

        max_area = max(range.area(), max_area);

        if (j == ranges.size()) break;
        for (; ranges[j].b - d > ranges[i].b && i < ranges.size(); i++)
            ;
