  for(int i=0;i<n;i++){
    scanf("%d%d%d",&x,&y,&b);
    in.push_back(make_pair(b,make_pair(x,y)));
  }

  sort(in.begin(),in.end());

  for(int i=0;i<n;i++){
    int pd = in[i].first;
    int px = in[i].second.first;
    int py = in[i].second.second;

    data[i+segn2-1] = NODE(pd,pd,px,px,py,py);
  }

  for(int i = segn2-2;i>=0;i--){
    data[i] = data[i*2+1] + data[i*2+2];
  }

  ll ans = 0,calc;
  NODE a;

  for(int i=0;i<n;i++){
    a = query(in[i].first,in[i].first+d);
    if(a.max_d < a.min_d) continue;
    calc = (ll)(a.max_x - a.min_x)*(ll)(a.max_y - a.min_y);
    ans = max(ans,calc);
  }

  cout << ans << endl;
  return 0;
}

