int max(int a, int b)
{
    return a > b ? a : b;
}

int main(int argc, const char *argv[])
{
    int N, d;
    scanf("%d %d", &N, &d);

    std::vector<Star> stars;
    for (int i = 0; i < N; i++)
    {
        int x, y, b;
        scanf("%d %d %d", &x, &y, &b);
        stars.push_back(Star(x, y, b));
    }
    std::sort(stars.begin(), stars.end());

    int max_area = 0;

    for (int i = 0; i < N; i++)
    {
        int min_x, max_x, min_y, max_y;
        min_x = stars[i].x;
        max_x = stars[i].x;
        min_y = stars[i].y;
        max_y = stars[i].y;

        for (int j = i; stars[j].b - stars[i].b <= d && j < N; j++)
        {
            min_x = min(min_x, stars[j].x);
            max_x = max(max_x, stars[j].x);
            min_y = min(min_y, stars[j].y);
            max_y = max(max_y, stars[j].y);
        }

        max_area = max((max_x - min_x) * (max_y - min_y), max_area);
    }

    printf("%d\n", max_area);

    return 0;
}

