		seglx.update(i, get<1>(star[i]));
		segly.update(i, get<2>(star[i]));
		segrx.update(i, -get<1>(star[i]));
		segry.update(i, -get<2>(star[i]));
	}

	lint res = 0;
	for (int left = 0, right = 0; left < n; left++){
