void solve(){
    int n,d;
    cin >> n >> d;
    map<int,vector<pii>> m;
    rep(i,0,n){
        int x,y,b;
        cin >> x >> y >> b;
        m[b].push_back(make_pair(x,y));
    }

    vector<int> v1[2],v2[2],ds; //,
    for(auto it=m.begin(); it!=m.end(); ++it){
        ds.push_back(it->first);
        int a=0,b=0,c=inf,d=inf;
        vector<pii> &tmp=it->second;
        rep(i,0,tmp.size()){
            a=max(a,tmp[i].second);
            b=max(b,tmp[i].first);
            c=min(c,tmp[i].second);
            d=min(d,tmp[i].first);
        }
        v1[0].push_back(a);
        v1[1].push_back(b);
        v2[0].push_back(c);
        v2[1].push_back(d);
    }

    ll ans=0,s=0;
    priority_queue<ll,vector<ll>> q1[2],del1[2];
    priority_queue<ll,vector<ll>,greater<ll>> q2[2],del2[2];
    rep(t,0,ds.size()){
        rep(i,0,2) q1[i].push(v1[i][t]);
        rep(i,0,2) q2[i].push(v2[i][t]);
        while(ds[t]-ds[s]>d){
            rep(i,0,2) del1[i].push(v1[i][s]);
            rep(i,0,2) del2[i].push(v2[i][s]);
            ++s;
        }
        rep(i,0,2){
            while(!q1[i].empty() and !del1[i].empty() and q1[i].top()==del1[i].top()){
                q1[i].pop();
                del1[i].pop();
            }
            while(!q2[i].empty() and !del2[i].empty() and q2[i].top()==del2[i].top()){
                q2[i].pop();
                del2[i].pop();
            }
        }
        ans=max(ans,(q1[0].top()-q2[0].top())*(q1[1].top()-q2[1].top()));
    }

    cout << ans << endl;
}

