int get(int a,int b,int n,int l,int r,int f){
  if(r<=a||b<=l)return -INF;
  if(a<=l&&r<=b)return tree[f][n];
  int m=(l+r)/2;
  return max(get(a,b,n*2+1,l,m,f),get(a,b,n*2+2,m,r,f));
}

void koshin(int i,int x,int f){
  i+=(1<<18)-1;
  tree[f][i]=x;
  while(i){
    i=(i-1)/2;
    tree[f][i]=max(tree[f][i*2+1],tree[f][i*2+2]);
  }
}

