using namespace std;

struct star {
  int x,y,b;
  star(int x,int y,int b) {
    this->x = x;
    this->y = y;
    this->b = b;
  }
};

struct sort_blight {
  bool operator()(const star& st1, const star& st2)
  {
      return st1.b < st2.b;
  }
};

int compute_stars_surface(vector<star>& stars,int st,int en) {
  int minx,miny,maxx,maxy;
  minx = maxx = stars[st].x;
  miny = maxy = stars[st].y;
  for(int i=st;i<=en;i++) {
    int x = stars[i].x;
    int y = stars[i].y;
    minx = min(minx,x);
    miny = min(miny,y);
    maxx = max(maxx,x);
    maxy = max(maxy,y);
  }
  return (maxx - minx) * (maxy - miny);
}

int main() {
  ios::sync_with_stdio(false);
  cin.tie(0);

  int N,d;
  vector<star> stars;

  cin >> N >> d;
  while(N--) {
    int x,y,b;
    cin >> x >> y >> b;
    star st(x,y,b);
    stars.push_back(st);
  }
  sort(stars.begin(),stars.end(),sort_blight());

  int st = 0;
  int en = 0;
  int max_surface = 0;
  while(en < stars.size()) {
    if(stars[en].b - stars[st].b <= d) {
      max_surface = max(max_surface,compute_stars_surface(stars,st,en));
      en++;
      st = 0;
    }
    else {
      st++;
    }
  }
  cout << max_surface << endl;
}

