		lint tans=(lint)(-tree[2].find_min(x,y)-tree[0].find_min(x,y))*(lint)(-tree[3].find_min(x,y)-tree[1].find_min(x,y));
		ans=max(ans,tans);
		if(E==B.end())break;
	}
	cout<<ans<<endl;

}

#include <bits/stdc++.h>

using namespace std;

pair<int, int> segx[1 << 19], segy[1 << 19];
int sz = (1 << 18);

void update(pair<int, int> *seg, int pos, int val)
{
    pos += sz - 1;
    seg[pos] = make_pair(val, val);

    while (pos){
        pos = (pos - 1) / 2;
        seg[pos].first = max(seg[pos * 2 + 1].first, seg[pos * 2 + 2].first);
        seg[pos].second = min(seg[pos * 2 + 1].second, seg[pos * 2 + 2].second);
    }
}

pair<int, int> get(pair<int, int> *seg, int a, int b, int k = 0, int l = 0, int r = sz)
{
    if (b <= l || r <= a) return (make_pair(INT_MIN, INT_MAX));
    if (a <= l && r <= b){
        return (seg[k]);
    }
    pair<int, int> lf = get(seg, a, b, k * 2 + 1, l, (l + r) / 2);
    pair<int, int> rg = get(seg, a, b, k * 2 + 2, (l + r) / 2, r);

    return (make_pair(max(lf.first, rg.first), min(lf.second, rg.second)));
}

int main()
{
    int N, d;

    scanf("%d %d", &N, &d);

    vector<pair<int, int> > xs, ys;
    vector<int> xss, yss, bs;

    for (int i = 0; i < N; i++){
        int x, y, b;
        scanf("%d %d %d", &x, &y, &b);
        xs.push_back(make_pair(b, x));
        ys.push_back(make_pair(b, y));
        bs.push_back(b);
    }

    sort(xs.begin(), xs.end());
    sort(ys.begin(), ys.end());

    for (int i = 0; i < N; i++){
        xss.push_back(xs[i].first);
        yss.push_back(ys[i].first);
        update(segx, i, xs[i].second);
        update(segy, i, ys[i].second);
    }

    long long ret = 0;

    for (int i = 0; i < N; i++){
        int xL = lower_bound(xss.begin(), xss.end(), bs[i])     - xss.begin(),
            xR = upper_bound(xss.begin(), xss.end(), bs[i] + d) - xss.begin();
        int yL = lower_bound(yss.begin(), yss.end(), bs[i])     - yss.begin(),
            yR = upper_bound(yss.begin(), yss.end(), bs[i] + d) - yss.begin();

        pair<int, int> posx = get(segx, xL, xR), posy = get(segy, yL, yR);
        ret = max(ret, (long long)(posx.second - posx.first) * (posy.second - posy.first));
    }

    printf("%lld\n", ret);

    return (0);
}

