struct SegmentTree
{

  vector< int > small, big;
  int sz;

  SegmentTree(int n)
  {
    sz = 1;
    while(sz < n) sz <<= 1;
    small.assign(2 * sz - 1, INF);
    big.assign(2 * sz - 1, -INF);
  }
  /*
  inline void Merge(int k)
  {
    small[k] = min(small[2 * k + 1] + add[2 * k + 1], small[2 * k + 2] + add[2 * k + 2]);
    big[k] = max(big[2 * k + 1] + add[2 * k + 1], big[2 * k + 2] + add[2 * k + 2]);
    sum[k] = sum[2 * k + 1] + add[2 * k + 1] + sum[2 * k + 2] + add[2 * k + 2];
    }*/
  inline int RangeMaximumQuery(int a, int b, int k, int l, int r)
  {
    if(a >= r || b <= l) return(-INF);
    if(a <= l && r <= b) return(big[k]);
    int L = RangeMaximumQuery(a, b, 2 * k + 1, l, (l + r) >> 1);
    int R = RangeMaximumQuery(a, b, 2 * k + 2, (l + r) >> 1, r);
    return(max(L, R));
  }
  inline int RangeMinimumQuery(int a, int b, int k, int l, int r)
  {
    if(a >= r || b <= l) return(INF);
    if(a <= l && r <= b) return(small[k]);
    int L = RangeMinimumQuery(a, b, 2 * k + 1, l, (l + r) >> 1);
    int R = RangeMinimumQuery(a, b, 2 * k + 2, (l + r) >> 1, r);
    return(min(L, R));
  }

  void Update(int k, int x)
  {
    k += sz - 1;
    small[k] = big[k] = x;
    while(k > 0) {
      k = (k - 1) >> 1;
    }
  }
  int RangeMaximumQuery(int a, int b)
  {
    return(RangeMaximumQuery(a, b, 0, 0, sz));
  }
  int RangeMinimumQuery(int a, int b)
  {
    return(RangeMinimumQuery(a, b, 0, 0, sz));
  }
};

