const int INF = (1 << 30) + (1 << 29);

struct SegmentTree {
    using F = function< int(int, int) >;
    vector< int > seg;
    int size;
    const F func;
    const int M1;
    SegmentTree(int n, const F f, const int &M) : func(f), M1(M) {
        size = 1; while (size < n) size <<= 1;
        seg.resize(2 * size - 1, M1);
    }
    void set(int k, int x) {seg[k + size - 1] = x;}
    void build() {
        for (int i = size - 2; i >= 0; --i) seg[i] = func(seg[2 * i + 1], seg[2 * i + 2]);
    }
    void update(int k, int x) {
        k += size - 1;
        seg[k] = x;
        while (k > 0) {
            k = (k - 1) / 2;
            seg[k] = func(seg[2 * k + 1], seg[2 * k + 2]);
        }
    }
    void add(int k, int x) {
        k += size - 1;
        seg[k] += x;
        while (k > 0) {
            k = (k - 1) / 2;
            seg[k] = func(seg[2 * k + 1], seg[2 * k + 2]);
        }
    }
    int query(int a, int b, int k = 0, int l = 0, int r = -1) {
        if (r < 0) r = size;
        if (r <= a || l >= b) return M1;
        if (l >= a && r <= b) return seg[k];
        int f_l = query(a, b, 2 * k + 1, l, (l + r) / 2);
        int f_r = query(a, b, 2 * k + 2, (l + r) / 2, r);
        return func(f_l, f_r);
    }
};

