        return (int)(itr - brightness.begin());
    };

    SegmentTree seg_x_min(N + 1, [](int a, int b){return min(a, b);}, INF);
    SegmentTree seg_x_max(N + 1, [](int a, int b){return max(a, b);}, -INF);
    SegmentTree seg_y_min(N + 1, [](int a, int b){return min(a, b);}, INF);
