#include <stdio.h>
#include <stdlib.h>

typedef struct { int x, y, b; } T;
T star[200002];

int cmp(T *a, T *b) { return a->b - b->b; }

long long calc(int i, int j)
{
	long long l, r, u, d;
	l = r = star[i].x, u = d = star[i].y;
	while (++i <= j) {
		if      (star[i].x < l) l = star[i].x;
		else if (star[i].x > r) r = star[i].x;
		if      (star[i].y < u) u = star[i].y;
		else if (star[i].y > d) d = star[i].y;
	}
	return (r-l)*(d-u);
}

int main()
{
	int n, d, i, j;
	long long ans, a;

	scanf("%d%d", &n, &d);
	for (i = 0; i < n; i++) scanf("%d%d%d", &star[i].x, &star[i].y, &star[i].b);
	qsort(star, n, sizeof(T), cmp);
	for (ans = 0, i = j = 0; j < n; ) {
		while (star[j].b - star[i].b > d) i++;
		while (j < n && star[j].b - star[i].b <= d) j++;
		if ((a = calc(i, j-1)) > ans) ans = a;
	}
	printf("%lld\n", ans);
	return 0;
}

