	for(i = 0; i < StarInput; i++){
		StarIndex[i] = i;
		scanf("%d", &X[i]);
		scanf("%d", &Y[i]);
		scanf("%d", &Bright[i]);
	}

	bubbleSortBright();
	printf("%lld\n", findMaxArea());

	return 0;
}

// From small to big
void bubbleSortBright(){
	int i, j;
	for(i = 0; i < StarInput; i++){
		for(j = i + 1; j < StarInput; j++){
			if(Bright[i] > Bright[j]){
				int temp = Bright[i];
				Bright[i] = Bright[j];
				Bright[j] = temp;
				temp = StarIndex[i];
				StarIndex[i] = StarIndex[j];
				StarIndex[j] = temp;
			}
		}
	}
	assignXY();
}

// assign acctual XY after sort
void assignXY(){
	int tempX[MAX_INPUT];
	int tempY[MAX_INPUT];
	int i;

	for(i = 0; i < StarInput; i++){
		tempX[i] = X[i];
		tempY[i] = Y[i];
	}
	for(i = 0; i < StarInput; i++){
		X[i] = tempX[StarIndex[i]];
		Y[i] = tempY[StarIndex[i]];
	}
}

long long findMaxArea(){
	long long maxArea = getArea(Bright[0]);
	long long tempArea;
	int i;

	for(i = 0 + 1; i < StarInput; i++){
		// Already goes to last element
		if(Bright[i] + BrightDiff > Bright[StarInput - 1]) break;
		// Next is too bright
		if(Bright[i + 1] - Bright[i] > BrightDiff) break;
		// Same brightness is already count
		if(Bright[i] == Bright[i - 1]) continue;

		tempArea = getArea(Bright[i]);
		if (tempArea > maxArea) maxArea = tempArea;
	}

	return maxArea;
}

long long getArea(int startBright){
	int minX = MAX_X;
	int minY = MAX_Y;
	int maxX = 0;
	int maxY = 0;
	int i;

	for(i = 0; i < StarInput; i++){
		// Check brightness
		if(Bright[i] < startBright) continue;
		if(Bright[i] - startBright > BrightDiff) break;

		// Check edge
		if(X[i] < minX)	minX = X[i];
		if(X[i] > maxX)	maxX = X[i];
		if(Y[i] < minY)	minY = Y[i];
		if(Y[i] > maxY)	maxY = Y[i];
	}

	return (long long)(maxX - minX) * (maxY - minY);
}

