abstract class SegmentTree<T>(val size:Int, private val array:Array<T>, private val zero:T){
    protected abstract fun accumulator(a:T, b:T):T
    companion object {
        internal fun Int.toSegmentTreeSize():Int{
            return when(this){
                0 -> 0
                1 -> 4
                else -> (this shr 1).toSegmentTreeSize() shl 1
            }
        }
    }
    operator fun set(index:Int, value:T):Unit{
        assert(index in 0 until size)
        var start = array.size / 2 + index
        array[start] = value
        while (start > 1){
            start /= 2
            array[start] = accumulator(array[start * 2], array[start * 2 + 1])
        }
    }
    operator fun get(from:Int, to:Int):T{
        assert(from in 0 until size && to in 0 until size)
        assert(from <= to)
        var left = from + array.size / 2
        var right = to + array.size / 2
        var res = zero
        while (left < right){
            if (left % 2 != 0) res = accumulator(res, array[left])
            if (right % 2 == 0) res = accumulator(res, array[right])
            left = (left + 1) / 2
            right = (right - 1) / 2
        }
        return if (left == right) accumulator(res, array[right]) else res

    }
    fun inspect():String = array.joinToString(" ")
}
class MaxSegmentTree(size:Int, defaultValue:Int = Int.MIN_VALUE):SegmentTree<Int>(size, Array(size.toSegmentTreeSize()){defaultValue}, defaultValue){
    override fun accumulator(a: Int, b: Int): Int {
        return if (a > b) a else b
    }
}
class MinSegmentTree(size:Int, defaultValue: Int = Int.MAX_VALUE):SegmentTree<Int>(size, Array(size.toSegmentTreeSize()){defaultValue}, defaultValue){
    override fun accumulator(a: Int, b: Int): Int {
        return if (a < b) a else b
    }
}
data class Star(val x:Int, val y:Int, val brightness:Int)
fun main(args:Array<String>):Unit {
    val (n, d) = readLine()!!.split(' ').map(String::toInt)
    val stars = Array(n){
        val (x, y, b) = readLine()!!.split(' ').map(String::toInt)
        Star(x, y, b)
    }.sortedBy(Star::brightness).toTypedArray()
    val maxX = MaxSegmentTree(n)
    val maxY = MaxSegmentTree(n)
    val minX = MinSegmentTree(n)
    val minY = MinSegmentTree(n)
    stars.forEachIndexed{index, star ->
        maxX[index] = star.x
        maxY[index] = star.y
        minX[index] = star.x
        minY[index] = star.y
    }
    var left = 0
    var right = stars.indexOfFirst { stars.first().brightness + d < it.brightness } - 1
    var maxSize = 0L
    while(right < n){
        if (left < right) {
            val tempArea = (maxX[left, right] - minX[left, right]).toLong() * (maxY[left, right] - minY[left, right]).toLong()
            if (tempArea > maxSize) maxSize = tempArea
        }
        if (right == n - 1) break
        val currentBrightness = stars[left].brightness
        while (++left < n && stars[left].brightness == currentBrightness){}
        if (left == n) break
        while (++right < n && stars[right].brightness <= stars[left].brightness + d){}
        --right
    }
    println(maxSize)
}

