import math
class SegmentTree:
    def __init__(self, dat, f, sentinel):
        self.pad = 2 ** math.ceil(math.log(len(dat),2))
        self.dat = [None] * self.pad + dat + [sentinel] * (self.pad - len(dat))
        self.f = f
        self.sentinel = sentinel
        for i in range(self.pad - 1, 0, -1):
            self.dat[i] = self.f(self.dat[i * 2:i * 2 + 2])

    def q(self,l,r):
        return self._q(self.pad + l,self.pad + r,1,self.pad,len(self.dat) - 1)
    def _q(self,a,b,k,l,r):
        if b < l or r < a:
            return self.sentinel
        elif a <= l and r <= b:
            return self.dat[k]
        else:
            vl = self._q(a,b,k * 2,l,(l+r)//2)
            vr = self._q(a,b,k * 2 + 1,(l+r)//2 + 1,r)
            return self.f(vl,vr)

