from copy import copy


class Bound:
    min_x = 2000001
    min_y = 2000001
    max_x = -1
    max_y = -1
    added = False

    def add(self, x, y):
        self.added = True
        if self.min_x > x: self.min_x = x
        if self.min_y > y: self.min_y = y
        if self.max_x < x: self.max_x = x
        if self.max_y < y: self.max_y = y

    def expand(self, bound):
        if bound.added:
            if not self.added:
                return copy(bound)
            if self.min_x > bound.min_x: self.min_x = bound.min_x
            if self.min_y > bound.min_y: self.min_y = bound.min_y
            if self.max_x < bound.max_x: self.max_x = bound.max_x
            if self.max_y < bound.max_y: self.max_y = bound.max_y
        return self

    def area(self):
        return (self.max_x - self.min_x) * (self.max_y - self.min_y) if self.max_x > -1 else 0

    @classmethod
    def expand_list(cls, bounds):
        return reduce(cls.expand, bounds[1:], copy(bounds[0]))

    def __str__(self):
        return '({},{},{},{})'.format(self.min_x, self.min_y, self.max_x, self.max_y)


class Bucket:
    def __init__(self, bounds, unit):
        self.unit = unit
        self.bucket = [self.create_unit(bounds, i, i + unit) for i in range(0, max_b + 1, unit)]

    @staticmethod
    def create_unit(bounds, s, t):
        b = bounds[s:t]
        return (Bound.expand_list(b), b)

    def get_range(self, s, t):
        unit = self.unit
        sd, sm = s // unit, s % unit
        td, tm = t // unit, t % unit
        if sd == td:
            return Bound.expand_list(self.bucket[sd][1][sm:tm + 1])
        else:
            b_list = [b[0] for b in self.bucket[sd + 1:td]]
            bound = Bound.expand_list(b_list) if b_list else Bound()
            s_list = self.bucket[sd][1][sm:]
            if s_list:
                bound = bound.expand(Bound.expand_list(s_list))
            t_list = self.bucket[td][1][:tm + 1]
            if t_list:
                bound = bound.expand(Bound.expand_list(t_list))
            return bound


