        r += 1
        if ceil(l / self.sq) >= r // self.sq:
            return self.f(self.dat[l:r])
        ret = self.f(self.baqet[ceil(l / self.sq):r // self.sq])
        if l % self.sq:
            ret = self.f(ret,self.f(self.dat[l:l + self.sq]))
        if r % self.sq:
            ret = self.f(ret,self.f(self.dat[r - r % self.sq:r]))
        return ret

