class SegmentTree:
    def __init__(self, dat, func, sentinel):
        self.pad = 2 ** math.ceil(math.log(len(dat),2)) - 1
        self.dat = [None] * self.pad + dat + [sentinel] * (self.pad + 1 - len(dat))
        self.func = func
        self.sentinel = sentinel
        for i in range(self.pad - 1, -1, -1):
            self.dat[i] = self.func(self.dat[i * 2 + 1],self.dat[i * 2 + 2])

    def q(self,s,e):
        return self._q(self.pad + s,self.pad + e,0,self.pad,len(self.dat) - 1)
    def _q(self,a,b,k,l,r):
        if b < l or r < a:
            return self.sentinel
        elif a <= l and r <= b:
            return self.dat[k]
        else:
            vl = self._q(a,b,k * 2 + 1,l,(l+r)//2)
            vr = self._q(a,b,k * 2 + 2,(l+r)//2 + 1,r)
            return self.func(vl,vr)
