class SegmentTree:
    left, right = None, None

    def __init__(self, n, bounds, is_min):
        func = min if is_min else max
        sentinel = min_sentinel if is_min else max_sentinel

        n2 = 2 ** ceil(log2(n))
        tree = [sentinel] * (n2 - 1) + bounds + [sentinel] * (n2 - n)
        for i in range(n2 - 2, -1, -1):
            tree[i] = func(tree[i * 2 + 1], tree[i * 2 + 2])
        self.tree = tree
        self.func = func
        self.sentinel = sentinel
        self.create_lr(n2)

    @classmethod
    def create_lr(cls, n2):
        if cls.left and cls.right:
            return
        left = [0] * (n2 - 1) + list(range(n2))
        right = [0] * (n2 - 1) + list(range(n2))
        for i in range(n2 - 2, -1, -1):
            left[i] = left[i * 2 + 1]
            right[i] = right[i * 2 + 2]
        cls.left = left
        cls.right = right

    @classmethod
    def get_indexes(cls, s, t, i=0):
        l, r = cls.left[i], cls.right[i]
        if l >= s and r <= t:
            return {i}
        if l > t or r < s:
            return set()
        return cls.get_indexes(s, t, i * 2 + 1) | cls.get_indexes(s, t, i * 2 + 2)

    def get(self, index_set):
        return self.func(self.tree[i] for i in index_set)


