class sqrt_tree:
    def __init__(self,dat,f):
        self.dat = dat
        self.sq = int(len(dat) ** 0.5)
        self.baqet = {}
        for fi in f:
            self.baqet[fi] = [fi(dat[i * self.sq:(i + 1) * self.sq]) for i in range(self.sq)]

    #query
    def q(self,f,l,r):
        r += 1
        if ceil(l / self.sq) >= r // self.sq:
            return f(self.dat[l:r])
        ret = f(self.baqet[f][ceil(l / self.sq):r // self.sq])
        if l % self.sq:
            ret = f(ret,f(self.dat[l:l + self.sq]))
        if r % self.sq:
            ret = f(ret,f(self.dat[r - r % self.sq:r]))
        return ret

