class SegTree:
  def __init__(self, num, lst):
    num_ = 1
    while num_ < num:
      num_ *= 2
    self.length = num_ * 2 - 1
    self.arr = [None] * (num_ * 2 - 1)
    for i in range(num):
      x1, x2, y1, y2 = lst[i]
      self.arr[num_ - 1 + i] = (x1, x2, y1, y2, i, i)

    self.make(0)

  def make(self, num):
    if num >= self.length:
      return None
    if self.arr[num] != None:
      return self.arr[num]
    else:
      self.arr[num] = self.con(self.make(num * 2 + 1), self.make(num * 2 + 2))
      return self.arr[num]

  def con(self, n1, n2):
    if n1 != None and n2 != None:
      return ((min(n1[0], n2[0]), max(n1[1], n2[1]), min(n1[2], n2[2]), max(n1[3], n2[3]), n1[4], n2[5]))
    if n1 != None:
      return n1
    if n2 != None:
      return n2
    return None

  def query(self, l, r, num):
    node = self.arr[num]
    if node == None:
      return None
    if l <= node[4] and node[5] <= r:
      return node[:4]
    elif r < node[4] or node[5] < l:
      return None
    else:
      x1 = self.query(l, r, num * 2 + 1)
      x2 = self.query(l, r, num * 2 + 2)
      if x1 == None and x2 == None:
        return None
      if x1 == None:
        return x2
      if x2 == None:
        return x1
      return (min(x1[0], x2[0]), max(x1[1], x2[1]), min(x1[2], x2[2]), max(x1[3], x2[3]))

