    num_ = 1
    while num_ < num:
      num_ *= 2
    self.length = num_ * 2 - 1
    self.arr = [None] * (num_ * 2 - 1)
    for i in range(num):
      self.arr[num_ - 1 + i] = Node(lst[i], (i, i))

    self.make(0)

  def make(self, num):
    if num >= self.length:
      return None
    if self.arr[num] != None:
      return self.arr[num]
    else:
      self.arr[num] = self.con(self.make(num * 2 + 1), self.make(num * 2 + 2))
      return self.arr[num]

  def con(self, n1, n2):
    if n1 != None and n2 != None:
      return Node((min(n1.minx, n2.minx), max(n1.maxx, n2.maxx), min(n1.miny, n2.miny), max(n1.maxy, n2.maxy)),
                  (n1.left, n2.right))
    if n1 != None:
      return n1
    if n2 != None:
      return n2
    return None

  def query(self, l, r, num):
    node = self.arr[num]
    if node == None:
      return None
    if l <= node.left and node.right <= r:
      return node
    elif r < node.left or node.right < l:
      return None
    else:
      return self.con(self.query(l, r, num * 2 + 1), self.query(l, r, num * 2 + 2))

