from math import ceil, log2


class SegmentTree:
    def __init__(self, n, bounds):
        n2 = 2 ** ceil(log2(n))
        tree = [None] * (n2 - 1) + bounds + [None] * (n2 - n)
        left = [0] * (n2 - 1) + list(range(n2))
        right = [0] * (n2 - 1) + list(range(n2))
        for i in range(n2 - 2, -1, -1):
            tree[i] = bound_expand(tree[i * 2 + 1], tree[i * 2 + 2])
            left[i] = left[i * 2 + 1]
            right[i] = right[i * 2 + 2]
        self.tree = tree
        self.left = left
        self.right = right

    def get(self, s, t, i=0):
        l, r = self.left[i], self.right[i]
        if l >= s and r <= t:
            return self.tree[i]
        if l > t or r < s:
            return None
        return bound_expand(self.get(s, t, i * 2 + 1), self.get(s, t, i * 2 + 2))


def bound_expand(b1, b2):
    if b1:
        if b2:
            return tuple(f(xy1, xy2) for f, xy1, xy2 in zip((min, min, max, max), b1, b2))
        return b1
    return b2


def bound_area(b):
    return (b[2] - b[0]) * (b[3] - b[1])


