#define REP(i, n) for(ll (i) = 0; (i) < (n); ++(i))
#define FOR(i, j, n) for(ll (i) = (j); (i) < (n); ++(i))
#define REPR(i, n) for(ll (i) = (n); (i) >= 0; --(i))

signed main(){
    int n,r,t;
    cin >> n >> r >> t;
    V a(n);
    for(auto& xx : a)
        cin >> xx;
    V v(r, 0);
    V minu(r, 0);
    V lazy(r, 0);
    V posvec(n, 0);
    REP(j,t){
        lazy = V(r, 0);
        REP(i,n){
            auto& pos = posvec[i];
            pos += a[i];
            pos %= r;
            if(j)
                lazy[pos]++;
            minu[pos] = min(minu[pos], --v[pos]);
        }
        REP(i,r)v[i] += lazy[i];
    }
    ll ans = 0;
    for(auto& xx : minu)ans -= xx;
    cout << ans << endl;
}

