	for (int i = 0; i < N; i++) {
		cin >> speed;
		for (int j = 0; j < T; j++) {
			dp[j][(speed*(j + 1)) % D]++;
			dp[j + 2][speed*(j + 2) % D]--;
		}
	}
	for (int i = 0; i < D; i++) {
