#include <bits/stdc++.h>

using namespace std;

int N, R, T;
int Pace[100], CurPos[100], EmptyCup[1000], FullCup[1000];

void solve() {
	int sum;

	sum = 0;
	for (int t = 1; t <= T; ++t) {
		for (int id = 0; id < N; ++id) {
			CurPos[id] += Pace[id];
			CurPos[id] %= R;
			if (t!=1) {
				++EmptyCup[CurPos[id]];
			}
			if (FullCup[CurPos[id]] != 0) {
				--FullCup[CurPos[id]];
			}
			else {
				++sum;
			}
		}
		for (int id = 0; id < N; ++id) {
			FullCup[CurPos[id]] += EmptyCup[CurPos[id]];
			EmptyCup[CurPos[id]] = 0;
		}
	}

	cout << sum << endl;
}

int main() {

	cin >> N >> R >> T;
	for (int i = 0; i < N; ++i) {
		cin >> Pace[i];
	}
	memset(CurPos, 0, sizeof(CurPos));
	memset(EmptyCup, 0, sizeof(EmptyCup));
	memset(FullCup, 0, sizeof(FullCup));
	solve();

	return 0;
}

