	REP(i, T) {
		REP(j, N) {
			//ここで一ターン
			locate[j] += p[j];
			locate[j] %= R;
			if (i != 0) empcup[locate[j]]++;
			if (cup[locate[j]] != 0) cup[locate[j]]--;
		}
		REP(j, R) {
			//空のやつを全部補充する
			cup[j] += empcup[j];
			empcup[j] = 0;
		}
	}

	REP(i, R) ans += cup[i] + empcup[i];
	ans += N;
	cout << ans << endl;
}

