int n, r, t, p[109], d[109], c[109];
int main() {
	cin >> n >> r >> t;
	for(int i = 0; i < n; i++) cin >> p[i], d[i] = p[i];
	queue<pair<int, int> > que;
	int ret = 0;
	for(int i = 1; i < t; i++) {
		while(que.size() && que.front().first == i) {
			c[que.front().second]++; que.pop();
		}
		for(int j = 0; j < n; j++) {
			if(c[d[j]] == 0) ret++;
			else c[d[j]]--;
			d[j] = (d[j] + p[j]) % r;
			que.push(make_pair(i + 2, d[j]));
		}
		for(int i = 0; i < r; i++) cout << c[i] << ' ';
		cout << endl;
	}
	cout << ret << endl;
