using namespace std;

int cnt[1000], need[1000], p[100];
int main() {
	int n, r, t; scanf("%d%d%d", &n, &r, &t);
	rep(i, n)scanf("%d", &p[i]);
	for (int i = 1; i <= t; i++) {
		rep(j, n) {
			int po = (p[j]*i) % r;
			if (!cnt[po])need[po]++;
			else cnt[po]--;
		}
		if (i == 1)continue;
		rep(j, n) {
			int po = (p[j] * i) % r;
			cnt[po]++;
		}
	}
	printf("%d\n", accumulate(need, need + 1000, 0));
}

