#include <stdio.h>
#include <string.h>

typedef struct { int c, p; } T;
T tbl[1002];
int e[1002], f[1002];

int main()
{
	int n, r, t, i, j, k, ans;

	scanf("%d%d%d", &n, &r, &t);
	for (i = 0; i < n; i++) scanf("%d", &tbl[i].p);
	for (ans = 0, i = 1; i <= t; i++) {
		for (j = 0; j < n; j++) {
			tbl[j].c = k = (tbl[j].c + tbl[j].p) % r;
			if (i > 1) e[k]++;
			if (f[k] > 0) f[k]--;
			else ans++;
		}
		for (j = 0; j < r; j++) {
			if (e[j] > 0) f[j] += e[j], e[j] = 0;
		}
	}
	printf("%d\n", ans);
	return 0;
}

