	int kara[1000];
	int i, j;
	int buin[100];
	int count = 0;
	if (scanf("%d%d%d", &N, &R, &T) != 3) return 1;
	for (i = 0; i < R; i++) haitteru[i] = kara[i] = 0;
	for (i = 0; i < N; i++) {
		if (scanf("%d", &p[i]) != 1) return 1;
		buin[i] = 0;
	}
	for (i = 0; i < T; i++) {
		for (j = 0; j < R; j++) {
			haitteru[j] += kara[j];
			kara[j] = 0;
		}
		for (j = 0; j < N; j++) {
			buin[j] = (buin[j] + p[j]) % R;
			if (i > 0) kara[buin[j]]++;
			if (haitteru[buin[j]] == 0) count++; else haitteru[buin[j]]--;
		}
	}
	printf("%d\n", count);
	return 0;
}

import java.util.Scanner;

class Main {
	public static void main(String[] args) {
		Scanner scan = new Scanner(System.in);

		int n, r, t, sum, pos;

		n = scan.nextInt();
		r = scan.nextInt();
		t = scan.nextInt();

		sum = 0;

		int[] p = new int[n];

		int[] water = new int[r];
		int[] empty = new int[r];

		for(int i = 0; i < n; i++) {
			p[i] = scan.nextInt();
		}

		for(int i = 1; i <= t; i++) {
			for(int j = 0; j < n; j++) {
				pos = (p[j] * i % r);

				if(i > 1) {
					empty[pos]++;
				}

				if(water[pos] > 0) {
					water[pos]--;

				} else {
					sum++;			
				}
			}

			for(int j = 0; j < r; j++) {
				if(empty[j] > 0) {
					water[j] += empty[j];
					empty[j] = 0;
				}
			}

		}

		System.out.println(sum);

		scan.close();
	}
}

using System.Collections.Generic;
using System;

public class hello
{
    public static void Main()
    {
        string[] line = Console.ReadLine().Trim().Split(' ');
        var n = int.Parse(line[0]);
        var r = int.Parse(line[1]);
        var t = int.Parse(line[2]);
        var p = new int[n];
        for (int i = 0; i < n; i++) p[i] = int.Parse(Console.ReadLine().Trim());
        var ans = goSim(n, r, t, p);
        Console.WriteLine(ans);
    }
    static int goSim(int n, int r, int t, int[] p)
    {
        var np = new int[r];
        var up = new int[r];
        var pp = new int[r];
        var ans = n;
        for (int i = 0; i < n; i++)
        {
            pp[i] = p[i];
            if (pp[i] >= r) pp[i] %= r; ;
        }
        var tt = 1;
        while (tt < t)
        {
            var plist = new List<int>();
            for (int i = 0; i < n; i++)
            {
                pp[i] += p[i];
                if (pp[i] >= r) pp[i] %= r;
                var ppi = pp[i];
                if (!plist.Contains(ppi)) plist.Add(ppi);
                up[ppi]++;
                if (np[ppi] >= 1) np[ppi]--;
                else ans++;
            }
            foreach (var x in plist)
                if (up[x] > 0) { np[x] += up[x]; up[x] = 0; }
            tt++;
        }
        return ans;
    }
}
