    int small = INF, large = -INF;
    for(int j = 0; j < S.size(); j += i) {
      int v = 0;
      for(int k = 0; k < i; k++) v = v * 10 + S[j + k] - '0';
      small = min(small, v);
      large = max(large, v);
      if(large - small >= ret) break;
    }
    ret = min(ret, large - small);
  }


  int small = INF, large = -INF;
  for(int i = 0; i < S.size(); i++) {
    if(S[i] == '1') {
      if(i + 1 >= S.size()) {
        small = INF, large = -INF;
      } else {
        large = max(large, S[i] - '0' + 10);
        ++i;
      }
    } else {
      small = min(small, S[i] - '0');
    }
  }

  if(small != INF && large != -INF) ret = min(ret, large - small);
  cout << ret << endl;
}

