int cal_min_diff(int length, const std::string& str) {
	for (auto i = 0; i < length - 2; ++i) {
		for (auto j = 1; j < str.size() / length; ++j) {
			if (str[i] != str[j * length + i]) return 10;
		}
	}
	int min = 100;
	int max = 0;
	for (auto i = 1; i <= str.size() / length; ++i) {
		auto val = (str[i * length - 2] - '0') * 10 + str[i * length - 1] - '0';
		min = std::min(val, min);
		max = std::max(val, max);
	}
	return max - min;
}
int count_single(const std::string& str) {
	int min_single = 10;
	int max_single = 0;
	for (const auto c : str) {
		min_single = std::min(min_single, c - '0');
		max_single = std::max(max_single, c - '0');
	}
	if (str.size() <= 2) return max_single - min_single;
	int min_double = 20;
	int max_double = 0;
	for (auto i = 0; i < str.size(); ++i) {
		auto val = str[i] - '0';
		if (str[i] == '1' && ++i < str.size()) {
			val *= 10;
			val += str[i] - '0';
		}
		min_double = std::min(min_double, val);
		max_double = std::max(max_double, val);
	}
	return std::min(max_single - min_single, max_double - min_double);
}
int main() {
	std::string str; std::cin >> str;
	int min_diff = count_single(str);
	for (auto i = 2; i < str.size(); ++i) if (str.size() % i == 0) {
		min_diff = std::min(min_diff, cal_min_diff(i, str));
	}
	std::cout << min_diff << '\n';
}

