const int INF = 1 << 29;

int sub(const string &ma, const string &mi) {
    assert(ma.size() == mi.size());
    int N = ma.size();
    for(int i=0; i<N; i++) {
        if(i < N - 2 and ma[i] != mi[i]) return INF;
    }

    int k = max(0, N - 2);
    int X = stoi(ma.substr(k)), Y = stoi(mi.substr(k));
    // fprintf(stderr, "ma = %s, mi = %s, X = %d, Y = %d\n",
    //        ma.c_str(), mi.c_str(), X, Y);
    return X - Y;
}

int solve1(const string &s, int d) {
    string ma(1, '0' - 1), mi(1, '9' + 1);
    int N = s.size();
    for(int i=0; i<N; i+=d) {
        string t = s.substr(i, d);
        ma = max(ma, t);
        mi = min(mi, t);
    }
    return sub(ma, mi);
}

int dp[100010][100];
int solve2(const string &s) {
    int N = s.size();
    fill(dp[0], dp[N+1], -INF);
    if(N > 1) dp[1][stoi(s.substr(0, 1))] = stoi(s.substr(0, 1));
    if(N > 2) dp[2][stoi(s.substr(0, 2))] = stoi(s.substr(0, 2));
    for(int i=1; i<N; i++) {
        for(int d=1; d<=2; d++) {
            if(i + d > N) continue;
            int v = stoi(s.substr(i, d));
            for(int j=0; j<100; j++) {
                // j (max), dp[i][j] (min), v
                int nj = max(j, v);
                int nk = min(dp[i][j], v);
                dp[i+d][nj] = max(dp[i+d][nj], nk);
            }
        }
    }

    int res = INF;
    for(int i=0; i<100; i++) {
        res = min(res, i - dp[N][i]);
    }
    return res;
}

int main() {
    string s; cin >> s;

    int N = s.size();
    vector<int> divs;
    for(int i=1; i*i<=N; i++) {
        if(N % i == 0) {
            divs.emplace_back(i);
            if(N / i != N) divs.emplace_back(N / i);
        }
    }

    int ans = 10;
    for(auto d : divs) ans = min(ans, solve1(s, d));
    // cerr << "# " << ans << endl;
    ans = min(ans, solve2(s));
    cout << ans << endl;
    return 0;
}

