bool f(std::string a, std::string b) {
	if(a.size() > b.size()) return true;
	else if(a.size() == b.size()) {
		for(int i = 0; i < a.size(); i++) {
			if(a[i] > b[i]) return true;
			if(a[i] < b[i]) return false;
		}
	}
	return false;
}

std::string max(std::string a, std::string b) {
	if(f(a, b)) return a;
	return b;
}

std::string min(std::string a, std::string b) {
	if(f(a, b)) return b;
	return a;
}

i64 calc(std::string a, std::string b) {
	for(int i = 0; i < a.size(); i++) {
		if(a[i] == b[i]) continue;
		if(a.size() - i > 18) return 1LL << 60;
		i64 A = stoll(a.substr(i, a.size() - i));
		i64 B = stoll(b.substr(i, b.size() - i));
		return A - B;
	}
	return 0;
}

int main() {
	std::string s; cin >> s;
	i64 ans = 1LL << 60;
	{
		int ma = 0, mi = 1 << 30, c = 0;
		for(int i = 0; i < s.size(); i++) {
			c++;
			if(s[i] == '1' and i + 1 < s.size()) {
				int tmp = stoi(s.substr(i, 2));
				ma = std::max(ma, tmp);
				mi = std::min(mi, tmp);
				i++;
			} else if(s[i]) {
				int tmp = s[i] - '0';
				ma = std::max(ma, tmp);
				mi = std::min(mi, tmp);
			}
		}

		if(c > 1) ans = ma - mi;
	}
	for(int i = 1; i * i <= s.size(); i++) {
		if(s.size() % i) continue;
		std::string ma = "", mi = std::string(s.size() + 1, '9');
		for(int j = 0; j < s.size(); j += i) {
			auto tmp = s.substr(j, i);
			ma = max(ma, tmp);
			mi = min(mi, tmp);
		}
		ans = std::min(ans, calc(ma, mi));

		if(i == 1) continue;
		ma = "", mi = std::string(s.size() + 1, '9');
		for(int j = 0; j < s.size(); j += s.size() / i) {
			auto tmp = s.substr(j, s.size() / i);
			ma = max(ma, tmp);
			mi = min(mi, tmp);
		}
		ans = std::min(ans, calc(ma, mi));
	}
	cout << ans << endl;
	return 0;
}

