	int asize(a.size());
	bool flag=false;
	bool rev_flag=false;
	{
		for (int i = 0; i < asize; ++i) {
			if (a[i] == b[i])continue;
			else {
				if (i >= asize - 3) {
					int anum=stoi(a.substr(i));
					int bnum=stoi(b.substr(i));
					return bnum-anum;
				}
				else {
					return 10000;
				}
			}
		}
	}

	return 0;
}

