#include<bits/stdc++.h>
using namespace std;

vector<int> div(auto n) {
  vector<int> d;
  for(auto i = 1; i * i <= n; ++i) {
    if(n % i) continue;
    d.emplace_back(i);
    if(i * i != n) d.emplace_back(n / i);
  }
  return d;
}

int toi(char c) {
  return c - '0';
}
int toi(string s) {
  int r = 0;
  for(auto c: s) r = 10 * r + toi(c);
  return r;
}

int dif(auto l, auto g) {
  auto n = l.size();
  if(2 < n) {
    if(l.substr(0, n-2) != g.substr(0, n-2)) return 10;
    l = l.substr(n-2, 2);
    g = g.substr(n-2, 2);
  }
  return toi(g) - toi(l);
}

int eq(auto n) {
  int r = 10;
  for(auto d: div(n.size())) if(d != n.size()) {
    string l = "~", g = "";
    for(auto i = 0; i < n.size(); i += d) {
      auto s = n.substr(i, d);
      l = min(l, s);
      g = max(g, s);
    }
    r = min(r, dif(l, g));
  }
  return r;
}

int neq(auto n) {
  int l = 10, g = 0;
  for(auto i = 0; i < n.size(); ++i) {
    auto t = toi(n[i]);
    if(t == 1) {
      if(i == n.size() - 1) return 10;
      t = 10 * t + toi(n[++i]);
    }
    l = min(l, t);
    g = max(g, t);
  }
  return g - l;
}

int main() {
  string n;
  cin >> n;
  cout << min(eq(n), neq(n)) << endl;
}

