inline int Check(int length)
{
    int Max = 0, Min = 1e9;
    for (int i = 1; i < len - length ; i+=length)
    {
        int Next = i + length;
        for (int j = i, k = Next; j <= Next - 2; j++, k++)
                if (str[j] != str[k]) return 1e9;
        Min = min(Min, str[Next-1]-'0');
        Min = min(Min, str[Next+length-1]-'0');
        Max = max(Max, str[Next-1]-'0');
        Max = max(Max, str[Next+length-1]-'0');
    }
    if (Min == 1e9) return Min;
    return Max-Min;
}

int main()
{
    scanf("%s", str+1);
    len = strlen(str+1);
    int Min = 1e9, Max = 0;
    for (int i=1; i<=len; i++) Min = min(Min, str[i]-'0'), Max=max(Max, str[i]-'0');
    int ans = Max-Min;
    for (int i=2; i<len; i++)
        if (len % i == 0)
            ans = min(ans, Check(i));
    if (len!=2)
    {
        int Max = 0, Min = 1e9;
        for (int i=1; i<=len; i++)
        {
