const int INF = 100100100100100;

using namespace std;

vector<int> divisors(int n){
	vector<int> res;
	for(int i = 1; i * i <= n; ++i){
		if(n % i != 0) continue;
		res.push_back(i);
		if(n / i == i) continue;
		res.push_back(n/i);
	}

	return res;
}

int f1(string s){
	int ret = INF;
	int n = (int)s.size();
	if(n == 2) return ret;
	vector<vector<int>> dp(n + 1, vector<int> (100, INF));
	dp[0][0] = INF - 1;
	for(int i = 0; i < n; i++){
		for(int curMa = 0; curMa < 100; curMa++){
			if(dp[i][curMa] == INF) continue;
			int curMi = dp[i][curMa];
			// ひとけたきる

			int val1 = stoi(s.substr(i, 1));
			//cout << i << " " << curMa << " " << curMi << " " << val1 << endl;
			int nxtMi = min(curMi, val1);
			int nxtMa = max(curMa, val1);
			//cout << nxtMi << " " << nxtMa << endl;
			//cout << i << "  from " << curMi << " " << curMa << " to " << nxtMi << " " << nxtMa << endl; 

			dp[i + 1][nxtMa] = min(dp[i + 1][nxtMa], nxtMi);

			if(i != n - 1){
				int val2;
				if(i != n - 1) val2 = stoi(s.substr(i, 2));
				nxtMi = min(curMi, val2);
				nxtMa = max(curMa, val2);
				dp[i + 2][nxtMa] = min(dp[i + 1][nxtMa], nxtMi);
				//cout << i << " " << i + 2 << "  from " << curMi << " " << curMa << " to " << nxtMi << " " << nxtMa << endl; 
			}
		}
	}

	/*
	for(int i = 0; i <= n; i++){
		for(int j = 0; j < 15; j++){
			cout << dp[i][j] << " ";
		}
		cout << endl;
	}
	cout << endl;
	*/


	for(int j = 0; j < 100; j++){
		if(dp[n][j] == INF) continue;
		//cout << "hoge " << j << " " << dp[n][j] << endl;
		ret = min(ret, j - dp[n][j]);
	}
	return ret;
}


int f2(string s){

	int ret = INF;
	int n = (int)s.size();
	auto div = divisors(n);

	for(auto i : div){
		if(i == n) continue;

		bool flag = true;
		if(i > 2){
			for(int j = 0; j < i - 2; j++){
				for(int k = 1; k < n / i; k++){
					int cur = (k - 1) * i + j;
					int nxt = k * i + j;
					if(s[cur] != s[nxt]){
						flag = false;
						break;
					}
				}
				if(!flag) break;
			}
		}

		if(!flag) continue;
		int mi = INF;
		int ma = 0;
		for(int j = 0; j < n; j += i){
			int idx = j;
			int getlen = i;
			if(i > 2){
				idx = j - 2 + i;
				getlen = 2;	
			}
			int val = stoi(s.substr(idx, getlen));
			mi = min(mi, val);
			ma = max(ma, val);
		}
		ret = min(ret, ma - mi);
	}

	return ret;
}


signed main(){

	string s; cin >> s;
	int ans = f1(s);
	ans = min(ans, f2(s));
	cout << ans << endl;

	return 0;
}

