long long val(string s){
	long long temp = 0;
	for(int i = 0;i < s.size();++i){
		temp *= 10;
		temp += s[i] - '0';
	}

	return temp;
}

int val_c(char c){
	return c - '0';
}

long long sub(string val1, string val2){
	long long temp1 = val(val1);
	long long temp2 = val(val2);

	return abs(temp1 - temp2);
}

long long checkEqual(string S){
	string mins, maxs;
	long long ans = 8;

	for(int k = 1;k < S.size();++k){
		if(S.size() % k != 0) continue;

		mins = maxs = S.substr(0, k);

		for(int s = 0;s < S.size();s += k){
			maxs = max(maxs, S.substr(s, k));
			mins = min(mins, S.substr(s, k));
		}

		ans = min(ans, sub(maxs, mins));

	}

	return ans;
}

long long check(string S){
	long long maxv = 0;
	long long minv = 10;
	long long p = 0, v;

	while(p < S.size()){
		v = val_c(S[p]);
		if(S[p] == '1' && p + 1 < S.size()){
			v = 10 + val_c(S[p + 1]);
			++p;
		}

		maxv = max(maxv, v);
		minv = min(minv, v);

		++p;
	}

	return abs(maxv - minv);
}

