vector<long long int> divisor(long long int n) {
	vector<long long int> res;
	for (long long int i = 1; i * i <= n; ++i) {
		if (n % i == 0) {
			res.push_back(i);
			if (i * i != n) res.push_back(n / i);
		}
	}
	sort(begin(res), end(res));
	return res;
}

int get_sa(string a, string b) {
	int asize(a.size());
	bool flag=false;
	bool rev_flag=false;
	{
		for (int i = 0; i < asize; ++i) {
			if (a[i] == b[i])continue;
			else {
				if (i >= asize - 3) {
					int anum=stoi(a.substr(i));
					int bnum=stoi(b.substr(i));
					return bnum-anum;
				}
				else {
					return 10000;
				}
			}
		}
	}

	return 0;
}

int main() {	
	string st;cin>>st;
	if (st.size() == 2) {
		cout<<abs(st[0]-st[1])<<endl;
		return 0;
	}
	int N(st.size());
	int ans=8;
	{
		vector<vector<vector<int>>>dp(N + 1, vector<vector<int>>(20, vector<int>(20)));
		dp[0][0][19] = 1;
		for (int l = 0; l < N; ++l) {
			for (int ma = 0; ma < 20; ++ma) {
				for (int mi = 0; mi < 20; ++mi) {
					if (!dp[l][ma][mi])continue;
					for (int use = 1; use <= 2; ++use) {
						if (l + use>N)continue;
						else {
							int num = stoi(st.substr(l, use));
							if (num >= 20)continue;
							dp[l + use][max(ma, num)][min(mi, num)] = true;
						}
					}
				}
			}
		}
		for (int ma = 0; ma < 20; ++ma) {
			for (int mi = 0; mi < 20; ++mi) {
				if (dp[N][ma][mi]) {
					ans = min(ans, ma - mi);
				}
			}
		}
	}
	auto divs(divisor(N));
	for (auto div : divs) {
		if(div==N)continue;
		int nans=0;
		vector<int>v(1,0);
		for (int i = 0; i < N / div-1; ++i) {
			string sta=st.substr(i*div,div);
			string stb=st.substr((i+1)*div,div);
			int sa=get_sa(sta,stb);
			if (abs(sa) > 10) {
				v.clear();
				break;
			}
			else {
				v.push_back(v.back()+sa);
			}
		}
		if (!v.empty()) {
			ans=min(ans,*max_element(v.begin(),v.end())-*min_element(v.begin(),v.end()));
		}
	}
	cout<<ans<<endl;
	return 0;
}

