void chmin(int &a, int b){
    a = min(a, b);
}

string s;
int n;

int match(int x){
    int m = n/x;
    int l;
    for(l=0;l<x;l++){
        for(int j=0;j<m;j++){
            if(s[l] != s[x*j+l]){
                return l;
            }
        }
    }
    return x;
}

int solve(int x){
    int l = match(x);
    if(x-l >= 3) return INF;
    if(x==l) return 0;
    int mi = INF, ma = 0;
    for(int i=0;i<n/x;i++){
        int tmp = stoi(s.substr(x*i+l,x-l));
        mi = min(mi, tmp);
        ma = max(ma, tmp);
    }
    return ma-mi;
}

int main(){
    cin >> s;
    n = s.size();
    if(n == 2){
        cout << abs(s[0]-s[1]) << endl;
        return 0;
    }
    int ans = INF;
    for(int i=2;i*i<=n;i++){
        if(n%i == 0){
            chmin(ans, solve(i));
            if(n/i != i) chmin(ans, solve(n/i));
        }
    }
    vector<vector<int> > dp(n+2, vector<int>(101, INF));
    dp[0][99] = 0;
    for(int i=0;i<n;i++){
        int d = s[i]-'0', dd = (i<n-1 ? stoi(s.substr(i,2)) : INF);
        for(int j=0;j<100;j++){
            chmin(dp[i+1][min(j,d)], max(dp[i][j], d));
            chmin(dp[i+2][min(j,dd)], max(dp[i][j], dd));
        }
    }
    for(int i=1;i<100;i++){
        chmin(ans, dp[n][i]-i);
    }
    cout << ans << endl;
    return 0;
}

