vector<int> divisors(int N) {
    vector<int> res;
    for(int d=1; d*d<=N; d++) {
        if(N % d == 0) {
            res.emplace_back(d);
            res.emplace_back(N/d);
        }
    }
    return res;
}

int sub(string A, string B) {
    reverse(A.begin(), A.end());
    reverse(B.begin(), B.end());

    int N = A.size();
    for(int i=2; i<N; i++) {
        if(A[i] != B[i]) return 100;
    }

    string sa = A.substr(0, 2), sb = B.substr(0, 2);
    reverse(sa.begin(), sa.end());
    reverse(sb.begin(), sb.end());
    // fprintf(stderr, "sa = %s, sb = %s\n", sa.c_str(), sb.c_str());
    return stoi(sa) - stoi(sb);
}

int solve1(string &s, int d) {
    string mi(d, '9'), ma(d, '0');
    int N = s.length();
    for(int i=0; i<N; i+=d) {
        string t = s.substr(i, d);
        mi = min(mi, t);
        ma = max(ma, t);
    }
    // fprintf(stderr, "d = %d, ma = %s, mi = %s\n", d, ma.c_str(), mi.c_str());
    return sub(ma, mi);
}

int solve2(string &s) {
    // i 桁目、最大 => 最小の最大値
    int N = s.size();
    vector< vector<int> > dp(N + 1, vector<int>(100, -1));
    if(N >= 1) dp[1][stoi(s.substr(0, 1))] = stoi(s.substr(0, 1));
    if(N >= 2 and N != 2) dp[2][stoi(s.substr(0, 2))] = stoi(s.substr(0, 2));

    for(int i=0; i<N; i++) {
        for(int j=0; j<100; j++) {
            if(dp[i][j] < 0) continue;
            for(int k=1; k<=2; k++) {
                if(i + k > N) continue;
                int val = stoi(s.substr(i, k));
                int a = j, b = dp[i][j];
                int ma = max({a, b, val});
                int mi = min({a, b, val});
                dp[i+k][ma] = max(dp[i+k][ma], mi);
            }
        }
    }

    int ans = 100;
    for(int i=0; i<100; i++) {
        if(dp[N][i] < 0) continue;
        ans = min(ans, i - dp[N][i]);
    }
    // fprintf(stderr, "ans = %d\n", ans);
    return ans;
}

int main() {
    string s; cin >> s;
    int N = s.length();

    int ans = 10;
    for(auto d : divisors(N)) {
        if(d == N) continue;
        ans = min(ans, solve1(s, d));
    }

    ans = min(ans, solve2(s));
    cout << ans << endl;
    return 0;
}

