using namespace std;

int def(string ma, string mi)
{
	for (int i = 0; i < 8; i++)
	{
		if (ma == mi)
			return i;
		mi[mi.size() - 1]++;
		for (int j = mi.size() - 1; j >= 0; j--)
		{
			if (mi[j] != '9' + 1)
				break;
			else if (j - 1 > 0)
				mi[j - 1]++;
			else
				mi.insert(0, "1");
		}
	}
	if (ma == mi)
		return 8;
	return 9;
}

int main()
{
	string n;
	cin >> n;
	int ans = 8, size = n.size();
	for (int i = 1; i < size; i++)
		if (size % i == 0)
		{
			string mi = n.substr(0, i), ma = n.substr(0, i);
			for (int j = 0; j < size; j += i)
			{
				ma = max(ma, n.substr(j, i));
				mi = min(mi, n.substr(j, i));
			}
			ans = min(ans, def(ma, mi));
		}
	int ma = 0, mi = 10, cnt = 0;
	while (cnt < size)
	{
		int tmp;
		if (n[cnt] == '1' && cnt + 1 < size)
		{
			tmp = 10 + (n[cnt + 1] - '0');
			cnt++;
		}
		else
			tmp = n[cnt] - '0';
		cnt++;
		ma = max(ma, tmp);
		mi = min(mi, tmp);
	}
	ans = min(ans, ma - mi);
	cout << ans << endl;

	return 0;
}

