const int INF = 1 << 28;

int main()
{
  string S;
  cin >> S;

  int ret = *max_element(begin(S), end(S)) - *min_element(begin(S), end(S));

  for(int i = 2; i < S.size(); i++) {
    if(S.size() % i) continue;
    string small, large;
    for(int j = 0; j < S.size(); j += i) {
      string v;
      for(int k = 0; k < i; k++) v += S[j + k];
      if(small.empty()) small = v, large = v;
      else small = min(small, v), large = max(large, v);
    }
    int latte = (*prev(large.end(), 2) - '0') * 10 + large.back() - '0';
    int malta = (*prev(small.end(), 2) - '0') * 10 + small.back() - '0';
    large.pop_back(), large.pop_back();
    small.pop_back(), small.pop_back();
    if(large == small) ret = min(ret, latte - malta);
  }
  int small = INF, large = -INF;
  for(int i = 0; i < S.size(); i++) {
    if(S[i] == '1') {
      if(i + 1 >= S.size()) {
        small = INF, large = -INF;
      } else {
        large = max(large, S[i + 1] - '0' + 10);
        ++i;
      }
    } else {
      small = min(small, S[i] - '0');
    }
  }

  if(small != INF && large != -INF) ret = min(ret, large - small);
  cout << ret << endl;
}

