char buf[100001];

int main(){

	scanf("%s",buf);

	int length;
	int ans = BIG_NUM;
	int tmp_min = BIG_NUM,tmp_max = 0,width;

	for(length = 0; buf[length] != '\0'; length++){
		tmp_min = min(tmp_min,buf[length]-'0');
		tmp_max = max(tmp_max,buf[length]-'0');
	}

	ans = tmp_max-tmp_min;

	if(ans == 0){
		printf("0\n");
		return 0;
	}

	vector<int> V;

	for(int i = 2; i <= sqrt(length); i++){
		if(length%i == 0){
			V.push_back(i);
			V.push_back(length/i);
		}
	}

	sort(V.begin(),V.end());
	V.erase(unique(V.begin(),V.end()),V.end());


	bool FLG;
	char base_num;
	int loc,tmp;

	for(int i = 0; i < V.size(); i++){
		width = V[i];
		tmp_min = BIG_NUM,tmp_max = 0;

		FLG = true;

		for(int k = 0; k < width-2; k++){
			base_num = buf[k];
			loc = k+width;

			while(loc < length){
				if(buf[loc] != base_num){
					FLG = false;
					break;
				}
				loc += width;
			}
			if(!FLG)break;
		}

		if(!FLG)continue;

		for(int k = 1; k <= length/V[i]; k++){
			tmp = 10*(buf[k*width-2]-'0')+buf[k*width-1]-'0';
			tmp_min = min(tmp_min,tmp);
			tmp_max = max(tmp_max,tmp);
		}
		ans = min(ans,tmp_max-tmp_min);
	}

	if(ans <= 2){
		printf("%d\n",ans);
		return 0;
	}

	tmp_min = BIG_NUM,tmp_max = 0;
	for(int i = 0; i < length;){
		if(buf[i] == '1'){
			if(buf[i+1] == '\0'){
				printf("%d\n",ans);
				return 0;
			}else{
				tmp = 10*(buf[i]-'0')+buf[i+1]-'0';
				tmp_min = min(tmp_min,tmp);
				tmp_max = max(tmp_max,tmp);
				i += 2;
			}
		}else{
			tmp_min = min(tmp_min,buf[i]-'0');
			tmp_max = max(tmp_max,buf[i]-'0');
			i += 1;
		}
	}

	ans = min(ans,tmp_max-tmp_min);

	printf("%d\n",ans);

	return 0;
}

#include <string>
