using namespace std;
int main() {
    string s;
    cin >> s;
    int n = s.size();
    vector<int> divs;
    for(int i = 1; i <= n; ++i) {
        if(n % i == 0) {
            divs.push_back(i);
        }
    }
    int ans = 10;
    for(int i : divs) {
        if(i == n) continue;
        string mn = "~", mx = " ";
        for(int j = 0; j < n; j += i) {
            string substr = s.substr(j, i);
            if(mn > substr) mn = substr;
            if(mx < substr) mx = substr;
        }
        //cout << i << ' ' << mn << ' ' << mx << endl;
        vector<int> val(i);
        for(int j = 0; j < i; ++j) {
            val[j] = int(mx[i - j - 1] - '0') - int(mn[i - j - 1] - '0');
        }
        for(int j = 0; j < i; ++j) {
            if(val[j] < 0) {
                val[j] += 10;
                val[j + 1] -= 1;
            }
        }
        bool isonedigit = true;
        for(int j = 1; j < i; ++j) {
            if(val[j] != 0) isonedigit = false;
        }
        if(isonedigit) {
            //cout << i << ' ' << val[0] << endl;
            ans = min(ans, val[0]);
        }
    }
    for(int i = 9; i >= 10 - ans; --i) {
        for(int j = 10; j <= i + ans; ++j) {
            vector<bool> dp(n + 1); dp[0] = true;
            for(int k = 1; k <= n; ++k) {
                // one-digit case
                int valone = int(s[k - 1] - '0');
                if(dp[k - 1] && i <= valone && valone <= j) dp[k] = true;
                if(k == 1) continue;
                int valtwo = int(s[k - 2] - '0') * 10 + int(s[k - 1] - '0');
                if(dp[k - 2] && i <= valtwo && valtwo <= j) dp[k] = true;
            }
            if(dp[n]) ans = min(ans, j - i);
        }
    }
    cout << ans << endl;
    return 0;
}

